import {useState, createContext, useContext } from "react";
import App from "../App";

const UserContext = createContext();
function UseContext() {

    const [user, setUser] = useState("John Doe");

    return (

        <UserContext.Provider  value={user}>
            <h1>{`Hello ${user}!`}</h1>
            <Component1 />
            


        </UserContext.Provider>

    );
}

function Component1(){
    return(
       <>
            <h1>Component 1</h1>
            <Component2 />

       </>
    )
}

function Component2(){
    return(
        <>
            <h1>Component 2</h1>
            <Component3 />
        </>
    )
}
function Component3(){
    return(
        <>
            <h1>Component 3</h1>
            <Component4/>
        </>
    )
}

function Component4(){
    return(
        <>
            <h1>Component 4</h1>
            <Component5 />
        </>
    )
}

function Component5(){
    const user = useContext(UserContext);
    return(
        <>
            <h1>Component 5</h1>
            <h2>{`Hello ${user} again!`}</h2>
        </>
    )
}


export  default UseContext;
